    def setup_method(self):
        self.game = Game()
        utttai_moves = [0, 1, 9, 2, 18, 3, 27, 4, 36, 5, 45, 10, 11, 19, 12,
                        28,
                        13, 37, 14, 46, 20, 21, 29, 22, 38, 23, 47, 30, 31, 39,
                        32, 48, 33, 54, 40, 41, 49, 42, 55, 43, 63, 44, 72, 50,
                        51, 56, 57, 58, 59, 60]
